def bellman_ford(graph, s):
    dist, pred, inf = {}, {}, float('inf')
    for v in graph:
        dist[v] = inf
        pred[v] = None
    dist[s] = 0
    edges = [(u, v) for u in graph for v in graph[u].keys()]
    for i in range(len(graph)-1):
